mapper.select:
	
	
	select *from
	(
	select rownum as RNUM, LIST_ALL.*
	from
	(
	select * from bookListView
	
	
			order by sales desc
	
	
			order by publication desc
	
	
			order by price asc
	
	
			order by price desc
	
	) LIST_ALL
	)

	where  RNUM> #{firstRecordIndex} 
	and RNUM<= #{firstRecordIndex}
	+ #{recordCountPerPage}

SELECT M.BOOK_NO, M.RENT_START, M.RENT_END, floor(M.RENT_END-SYSDATE) AS "RENTDATE", B.BOOK_FILE_NAME, B.COVER_FILE_NAME, B.TITLE, B.PUBLISHER, B.WRITER, B.PUBLICATION, B.GENRE, B.SUMMARY
  FROM MY_BOOKS M, BOOK B
 WHERE M.BOOK_NO=B.BOOK_NO AND USER_ID=#{userId}

select *from
	(
	select rownum as RNUM, LIST_ALL.*
	from
	(
	SELECT M.BOOK_NO, M.RENT_START, M.RENT_END, floor(M.RENT_END-SYSDATE) AS "RENTDATE", B.BOOK_FILE_NAME, B.COVER_FILE_NAME, B.TITLE, B.PRICE, B.PUBLISHER, B.WRITER, B.PUBLICATION, B.GENRE, B.SUMMARY
	  FROM MY_BOOKS M, BOOK B
	 WHERE M.BOOK_NO=B.BOOK_NO AND USER_ID=#{userId}
	 
	 and title like '%' || #{searchKeyword} || '%' or writer like '%' || #{searchKeyword} || '%' or publisher like '%' || #{searchKeyword} || '%'
	 
	 ) LIST_ALL
	)
	 
	where RNUM> #{firstRecordIndex} 
	and RNUM<= #{firstRecordIndex}
	+ #{recordCountPerPage}

mapper.update:
update book
   set TITLE=#{title}, PUBLISHER=#{publisher},WRITER=#{writer}, PUBLICATION=#{publication},GENRE=#{genre}, PRICE, SALES=#{price},BOOK_FILE_NAME=#{bookFileName}, COVER_FILE_NAME=#{coverFileName}, BOOK_FILE_SIZE=#{bookFileSize},SUMMARY=#{summary}
   where book_no=#{bookNo}
