mapper.update:
update notice_board
set
title=#{title} ,content=#{content}
where notice_no =#{notice_no}

mapper.select:
 
		select *from
		(
		select rownum as RNUM, LIST_ALL.*
		from
		(
			select * from notice_board
			
			order by Notice_No desc
		) LIST_ALL
		)
	
	where  RNUM> #{firstRecordIndex} 
	and RNUM<= #{firstRecordIndex}
	+ #{recordCountPerPage}
